// HomePage.js
import React from 'react';
import { Link } from 'react-router-dom';
import './HomePage.css'; // Importa el fitxer CSS

function HomePage() {
  return (
    <div className="homepage-container">
      <div>
        <input type="text" placeholder="Què busques" className="search-input" />
      </div>
      <div className="nav-container">
        <div className="button-container">
          <button className="nav-button">Boxa</button>
          <button className="nav-button">Sacs</button>
          <button className="nav-button">Guants</button>
          <button className="nav-button">Altres</button>
        </div>
        <Link to="/cart" className="cart-link">Carrito</Link> {/* Afegeix un enllaç a la pàgina del carret de compra */}
      </div>
      <div className="new-container">
        {Array(16).fill().map((_, i) => (
          <button key={i} className="new-element">
            <div className="square"></div>
          </button>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
